#include <string>
#include <iostream>

int calculate(std::string input) {
    int out = 0;
    for (int i = 0; i < input.length(); i++) {
        if(out <0){
            return i;
        }
        if (input[i] == '('){
            out++;
        } else if (input[i] == ')'){
            out --;
        }
    }
    return out;
}


